import React, { useState } from "react";

function Home() {
  // Assuming you have some kind of authentication state
  const [isAuthenticated, setIsAuthenticated] = useState(true); // Set this to the initial authentication state

  const handleSignOut = () => {
    // Perform sign-out actions here, e.g., clear authentication state
    setIsAuthenticated(false);
  };

  return (
    <div className="home">
      <h2>Welcome to our website (After login)</h2>
      {isAuthenticated ? (
        <button onClick={handleSignOut}>Sign out</button>
      ) : (
        <p>You are signed out.</p>
      )}
    </div>
  );
}

export default Home;
