import React, { useState } from "react"

function Login() {
  const [login, setLogin] = useState(true)
  const [displayContainer, setDisplayContainer] = useState(true)
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [loggedIn, setLoggedIn] = useState(false)

  const handleSubmitForm = () => {
    if (login) {
      if (storedEmail === email && storedPassword === password) {
        setLoggedIn(true)
        setDisplayContainer(false) // Hide the login/signup form
      } else {
        alert("Login failed")
      }
    } else {
      // Signup Button has been clicked
      alert("Signup successful")
    }
  }

  return (
    <div>
      {displayContainer && (
        <div>
          <div className="form-container">
            <div className={login ? "form-container-active" : ""} 
                onClick={() => setLogin(true)}
            >
              <h1>Login</h1>
            </div>
            <div className={!login ? "form-container-active" : ""}
              onClick={() => setLogin(false)}
            >
              <h1>Signup</h1>
            </div>
          </div>

          <form>
            {!login && (
              <input type="text" placeholder="Enter name" value={name} onChange={(e) => setName(e.target.value)} />
            )}
            
            <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} /> 
            <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} /> 
            
            <button type="button" onClick={handleSubmitForm}> {login ? "LOGIN" : "SIGNUP"} </button>
          </form>

          {login && <h4>Forgot Password?</h4>}
          <h5>or {login ? "login" : "signup"} with</h5>

          <h5>
            Don't have an Account?{" "}
            <button id="createAccountBtn" onClick={() => setLogin(false)}>
              Create new account
            </button>
          </h5>
        </div>
      )}

      {loggedIn && (
        <div className="afterLogin">
          <h2>Welcome to our website</h2>
          <h2>Hello {localStorage.getItem("name")}</h2>
          <h2>Email id {localStorage.getItem("email")}</h2>
        </div>
      )}
    </div>
  )
}

export default Login
